definition.script:
pipeline
{
    environment {
        registry = "liviorodrigues/my_app:beta"
        registryCredential = 'docker_access'
        dockerImage = ''
    }
    agent any

    stages
    {
        stage('Clean Workspace') {
            steps {
                deleteDir()
              }
        }

        stage('Cloning Git') {
            steps {
                git branch: 'master',
                credentialsId: 'user_pass_gitlab',
                url: 'http://172.17.177.40:10080/jenkis/my_app.git'
                sh "ls  -la"
              }
        }

        stage('Unity Test') {
            steps {
                echo "Ã‹xec Unity Test/Jacoco ... "
              }
        }

        stage('Docker Remote Build') {
            steps {
                sh 'echo "Oi Mundo"'
                script {
                    try {
                        sh 'docker build -t my-hello-world .'
                        sh 'docker run -d -p 8080:8080 my-hello-world'
                    } catch (Exception e) {
                        sh "echo $e"
                    }
                }
            }
        }

        stage('SonarQube Analysis') {
          steps {
            withSonarQubeEnv('sonar') {
                sh "mvn sonar:sonar -Dsonar.host.url=http://172.17.177.44"
            }
          }
        }

        stage('Deploy Data') {
            steps {
                 sh "ansible --version"
                 sh "ansible-playbook -u vagrant -i hosts app_provisioning.yml"
                 sh "ls"
            }
        }

        stage('Notify GitLab') {
            steps {
                echo 'Notify GitLab'
                updateGitlabCommitStatus name: 'build', state: 'pending'
                updateGitlabCommitStatus name: 'build', state: 'success'
            }
        }
    }
}
